# Step 1: Join all text
all_text = ' '.join(data)

# Step 2: Split words (keeping punctuation as part of the word)
words = all_text.split()

# Step 3: Optionally remove duplicates
unique_words = sorted(set(words))

# Step 4: Save to file
with open("tamil_words.txt", "w", encoding="utf-8") as f:
    for word in unique_words:
        f.write(word + "\n")
